import { defaultAvatarStyle } from "./defaults";
import unknown from "./unknown.png";

import type { ActorHeaderInfo } from "@bovine/muck-out-types";
import { determineAvatarLink } from "./util";

type ActorAvatarProps = {
  /** Actor to display */
  actorInfo: ActorHeaderInfo;
  style?: object;
};

function ActorAvatar({
  actorInfo,
  style = defaultAvatarStyle,
}: ActorAvatarProps) {
  const avatar = actorInfo.avatarUrl || unknown;
  const url = determineAvatarLink(actorInfo);

  return (
    <a href={url}>
      <img
        style={{
          width: "50px",
          height: "50px",
          borderRadius: "30%",
          marginRight: "10px",
          ...style,
        }}
        src={avatar}
      />
    </a>
  );
}

export { ActorAvatar, type ActorAvatarProps };
