import type { ActorHeaderInfo } from "@bovine/muck-out-types";
import { ActorAvatar } from "../ActorAvatar";
import { defaultAvatarListStyle, defaultAvatarStyle } from "./defaults";

type ActorAvatarListProps = {
  actors: ActorHeaderInfo[];
  avatarStyle?: object;
  style?: object;
};

function ActorAvatarList({
  actors,
  avatarStyle = defaultAvatarStyle,
  style = defaultAvatarListStyle,
}: ActorAvatarListProps) {
  return (
    <div style={{ ...defaultAvatarListStyle, ...style }}>
      {actors.map((x) => (
        <ActorAvatar actorInfo={x} key={x.id} style={avatarStyle} />
      ))}
    </div>
  );
}

export { ActorAvatarList, type ActorAvatarListProps };
