import type { ActorHeaderInfo } from "@bovine/muck-out-types";
import { ActorHeader, ObjectMeta } from "..";
import type { ObjectMetaInfo } from "@bovine/muck-out-types";

type NoteObject = {
  content: string;
  actorInfo: ActorHeaderInfo;
  metaInfo: ObjectMetaInfo;
};

type SimpleNoteProps = {
  data: NoteObject;
};

function SimpleNote({ data }: SimpleNoteProps) {
  return (
    <div
      className="post"
      style={{ border: "2px black solid", margin: "20px", padding: "20px" }}
    >
      <div style={{ display: "flex" }}>
        <ActorHeader actorInfo={data.actorInfo} style={{ flexGrow: 1 }} />
        <ObjectMeta info={data.metaInfo} />
      </div>
      <div dangerouslySetInnerHTML={{ __html: data.content }} />
    </div>
  );
}

export { SimpleNote, type SimpleNoteProps };
