def update_map(self):
    self.costume = nb.google_maps.get_map(self.lat, self.long, self.width, self.height, self.zoom)
    nb.send_message('Map Updated')

@onstart()
def my_onstart(self):
    self.zoom = 9
    self.lat, self.long = get_location()
    self.update_map()

@onkey(keys.right)
def on_right_arrow(self):
    self.long = nb.google_maps.get_longitude_from_x(self.width / 2)
    self.update_map()

@onkey(keys.left)
def on_left_arrow(self):
    self.long = nb.google_maps.get_longitude_from_x(self.width / -2)
    self.update_map()

@onkey(keys.up)
def on_up_arrow(self):
    self.lat = nb.google_maps.get_latitude_from_y(self.height / 2)
    self.update_map()

@onkey(keys.down)
def on_down_arrow(self):
    self.lat = nb.google_maps.get_latitude_from_y(self.height / -2)
    self.update_map()

@onkey(keys.minus)
def on_zoom_out(self):
    if self.zoom > 1:
        self.zoom -= 1
        self.update_map()

@onkey(keys.plus)
def on_zoom_in(self):
    if self.zoom < 25:
        self.zoom += 1
        self.update_map()
