class Token:

    def __init__(self, type, value, line=0, column=0):
        self.type = type
        self.value = value
        self.line = line
        self.column = column

    def __str__(self):
        return f"Token({self.type}, {repr(self.value)}, line={self.line}, column={self.column})"

    def __repr__(self):
        return self.__str__()
