def _cached_create_nba_nba_team_model(
    row: pd.Series,
    home: bool,
    session: requests_cache.CachedSession,
    dt: datetime.datetime,
    league: League,
    league_id: str,
    version: str,
) -> TeamModel | None:
    return _create_nba_nba_team_model(
        row=row,
        home=home,
        session=session,
        dt=dt,
        league=league,
        league_id=league_id,
        version=version,
    )


def create_nba_nba_team_model(
    row: pd.Series,
    home: bool,
    session: requests_cache.CachedSession,
    dt: datetime.datetime,
    league: League,
    league_id: str,
) -> TeamModel | None:
    """Create a team model from NBA API."""
    if not pytest_is_running.is_running() and dt < datetime.datetime.now().replace(
        tzinfo=dt.tzinfo
    ) - datetime.timedelta(days=7):
        return _cached_create_nba_nba_team_model(
            row=row,
            home=home,
            session=session,
            dt=dt,
            league=league,
            league_id=league_id,
            version=VERSION,
        )
    with session.cache_disabled():
        return _create_nba_nba_team_model(
            row=row,
            home=home,
            session=session,
            dt=dt,
            league=league,
            league_id=league_id,
            version=VERSION,
        )
