def 只取数字(源文本):
    """
    从源文本中提取所有数字字符，并返回一个只包含数字的字符串。

    参数：
        - 源文本 (str): 包含要提取数字的源文本。

    返回值：
        - str: 只包含源文本中所有数字字符的字符串。如果出现异常，返回空字符串。

    示例：
        源文本 = "这是一个示例文本，包含一些数字：1234567890"
        数字文本 = zfx_textutils.只取数字(源文本)
        print("提取的数字文本:", 数字文本)  # 输出：1234567890
    """
    try:
        # 使用 filter 函数和 str.isdigit 提取所有数字字符
        数字列表 = ''.join(filter(str.isdigit, 源文本))
        return 数字列表
    except Exception:
        return ""  # 发生异常时返回空字符串